import React, { useState } from 'react';
import './Right.css';

const Right = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(false);
  const [error, setError] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();

    // Basic validation
    if (!email || !password) {
      setError('Please enter both email and password.');
      return;
    }

    setError('');

    // Simulate login logic
    console.log('Login details:', {
      email,
      password,
      remember,
      role: 'faculty'
    });

    alert('Login successful (simulated)!');
  };

  const handleGoogleLogin = () => {
    alert('Google login clicked (simulated)');
  };

  return (
    <div className="right-panel">
      <div className="role-toggle">
        <button className="role-btn">Student</button>
        <button className="role-btn active">Faculty</button>
      </div>

      <h2 className="login-heading">Logging in as Faculty</h2>

      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Faculty ID or Email</label>
          <input
            type="text"
            placeholder="Enter your Faculty ID or email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            type="button"
            className="forgot-link"
            onClick={() => alert('Password reset link clicked')}
          >
            Forgot password?
          </button>
        </div>

        <div className="remember-section">
          <input
            type="checkbox"
            id="remember"
            checked={remember}
            onChange={() => setRemember(!remember)}
          />
          <label htmlFor="remember">Remember me</label>
        </div>

        {error && <p style={{ color: 'red', fontSize: '13px' }}>{error}</p>}

        <button type="submit" className="login-btn">Login</button>
      </form>

      <div className="divider">or continue with</div>

      <button className="google-btn" onClick={handleGoogleLogin}>
        <img src="/images/google-icon.png" alt="Google" />
        Log In with Google
      </button>
    </div>
  );
};

export default Right;
